package sharding.sphere.demo.mapper;import java.util.List;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Result;import org.apache.ibatis.annotations.Results;import org.apache.ibatis.annotations.Select;import sharding.sphere.demo.entity.User;@Mapperpublic interface UserMapper {    @Insert(            "<script>"                    + "INSERT IGNORE INTO t_encrypted_user(user_name, phone_number, id_number, encrypted_user_name, encrypted_phone_number, encrypted_id_number) VALUES "                    + "<foreach collection='userList' item='user' separator=','>"                    + "(#{user.userName}, #{user.phoneNumber}, #{user.idNumber}, #{user.encryptedUserName}, #{user.encryptedPhoneNumber}, #{user.encryptedIdNumber})"                    + "</foreach>"                    + "</script>")    int batchInsertEncryptedUser(@Param("userList") List<User> userList);    @Insert(            "<script>"                    + "INSERT IGNORE INTO t_user(user_name, phone_number, id_number) VALUES "                    + "<foreach collection='userList' item='user' separator=','>"                    + "(#{user.userName}, #{user.phoneNumber}, #{user.idNumber})"                    + "</foreach>"                    + "</script>")    int batchInsertUser(@Param("userList") List<User> userList);    @Insert(            "INSERT IGNORE INTO t_encrypted_user(user_name, phone_number, id_number, encrypted_user_name, encrypted_phone_number, encrypted_id_number) "                    + "VALUES (#{user.userName}, #{user.phoneNumber}, #{user.idNumber}, #{user.encryptedUserName}, #{user.encryptedPhoneNumber}, #{user.encryptedIdNumber})")    int insertEncryptedUser(@Param("user") User user);    @Insert(            "INSERT IGNORE INTO t_user(user_name, phone_number, id_number) "                    + "VALUES (#{user.userName}, #{user.phoneNumber}, #{user.idNumber})")    int insertUser(@Param("user") User user);    @Insert("DELETE FROM t_encrypted_user WHERE ${fieldName} = #{fieldValue}")    int deleteEncryptedUser(            @Param("fieldName") String fieldName, @Param("fieldValue") String fieldValue);    @Insert("DELETE FROM t_user WHERE ${fieldName} = #{fieldValue}")    int deleteUser(@Param("fieldName") String fieldName, @Param("fieldValue") String fieldValue);    @Select("SELECT * FROM t_encrypted_user WHERE ${fieldName} = #{fieldValue}")    @Results({        @Result(property = "userName", column = "user_name"),        @Result(property = "phoneNumber", column = "phone_number"),        @Result(property = "idNumber", column = "id_number"),        @Result(property = "encryptedUserName", column = "encrypted_user_name"),        @Result(property = "encryptedPhoneNumber", column = "encrypted_phone_number"),        @Result(property = "encryptedIdNumber", column = "encrypted_id_number"),        @Result(property = "id", column = "id")    })    List<User> findEncryptedUser(            @Param("fieldName") String fieldName, @Param("fieldValue") String fieldValue);    @Select("SELECT * FROM t_user WHERE ${fieldName} = #{fieldValue}")    @Results({        @Result(property = "userName", column = "user_name"),        @Result(property = "phoneNumber", column = "phone_number"),        @Result(property = "idNumber", column = "id_number"),        @Result(property = "id", column = "id")    })    List<User> findUser(            @Param("fieldName") String fieldName, @Param("fieldValue") String fieldValue);}